mapper.select:
select id, sResumeID, sPositionID, sUserID, sHRID, iStatus, dCreateTime, dUpdateTime from t_application where 1=1
and sUserID = #{sUserID}
and sHRID = #{sHRID}

mapper.insert:
insert into
    t_application
(
    id, sResumeID, sPositionID, sUserID, sHRID, iStatus, dCreateTime, dUpdateTime
)
values
(
    #{id},#{sResumeID},#{sPositionID},#{sUserID},#{sHRID}, #{iStatus}, #{dCreateTime}, #{dUpdateTime}
)

mapper.select:
select
    count(1)
from
    t_application
where
    1=1

    and sPositionID = #{sPositionID}


    and sResumeID = #{sResumeID}


    and sUserID = #{sUserID}


    and sHRID = #{sHRID}

select id, sResumeID, sPositionID, sUserID, sHRID, iStatus, dCreateTime, dUpdateTime from t_application where 1 =1
and sUserID = #{sUserID}
and sHRID = #{sHRID}

mapper.delete:
delete from
    t_application
where
    sPositionID = #{sPositionID}
and
    sUserID = #{sUserID}
