mapper.select:
select
    id, sUserID, sPositionID, dCreateTIme, dUpdateTime
from
    t_collection
where
    1=1

    and id = #{id}


    and sPositionID = #{sPositionID}


    and sUserID = #{sUserID}

mapper.insert:
insert into
    t_collection
(
    id, sUserID, sPositionID, dCreateTIme, dUpdateTime
)
values
(
    #{id},#{sUserID},#{sPositionID},#{dCreateTime},#{dUpdateTime}
)

mapper.select:
select count(1) from t_collection where 1=1
and sPositionID = #{sPositionID}
and sUserID = #{sUserID}

mapper.delete:
delete from
    t_collection
where
    sPositionID = #{sPositionID}
and
    sUserID = #{sUserID}
