mapper.insert:
insert into
    t_comment
(
    id, sContent, sUserID, sPositionID, sParentID, dCreateTime, dUpdateTime
)
values
(
    #{id}, #{sContent}, #{sUserID}, #{sPositionID}, #{sParentID}, #{dCreateTime}, #{dUpdateTime}
)

mapper.select:
select
    count(1)
from
    t_comment
where
    1=1

    and sParentID = #{sParentID}


    and sPositionID = #{sPositionID}


    and sUserID = #{sUserID}
