mapper.select:
select id, sUsername, sPassword, sRealName, sPhone, sEmail, iGender, iAge, sAvatar, sProvince, sCity, iGraduationYear, sMajor, sEducation, sSchoolID, iRoleType, iStatus, sDirection, sDescription, dCreateTime, dUpdateTime from t_user
AND id = #{id}
and sUsername = #{sUsername}
and sPassword = #{sPassword}
and sEmail = #{sEmail}
and sPhone = #{sPhone}

mapper.delete:
delete from
	t_user
where 1=1

	and id = #{id}


	and id in

		#{id}

mapper.select:
select count(1) from t_user where 1=1
and sUsername = #{sUsername}
and sDirection = #{sDirection}
